mapper.select:
select n.*, nv.seen, ud.nome as from_user_name, ud.cognome
as
from_user_surname
from notifications n, notification_view nv,
users_details ud
where n.notification_id = nv.notification_ref
and
nv.ref_to = #{user_id}
and ud.user_ref = n.from_user
order by
n.notification_id desc
limit #{limit}
offset #{offset}

select n.*, nv.seen, ud.nome as from_user_name, ud.cognome
as
from_user_surname
from notifications n, notification_view nv,
users_details ud
where n.notification_id = nv.notification_ref
and nv.ref_to = #{user_id}
and ud.user_ref = nv.ref_to
and n.notification_id = #{notificationId}

mapper.update:
update notification_view
set seen = true
from notifications
where notifications.notification_id = notification_view.notification_ref
and notifications.notification_id = #{notificationId}
and notification_view.ref_to = #{user_id}

mapper.select:
select nv.ref_to
from notification_view nv, notifications n
where n.notification_id = #{notificationId}
and n.notification_id =
nv.notification_ref
and nv.ref_to = #{user_id}
