mapper.update:
update notification_view
set seen = true
from notifications
where notifications.notification_id = notification_view.notification_ref
and notifications.notification_id = #{notificationId}
and notification_view.ref_to = #{user_id}

mapper.select:
select nv.ref_to
from notification_view nv, notifications n
where n.notification_id = #{notificationId}
and n.notification_id =
nv.notification_ref
and nv.ref_to = #{user_id}
